(() => {
  // javascript/script.js
  var ss;
  (function($) {
    ss = {
      init: function() {
        try {
          let initBgCover2 = function(context) {
            if (!context) context = $("body");
            context.find(".js-has-bg-cover").each(function() {
              var holder = $(this);
              var image = holder.find("> img").hide();
              var imageSrc = image.prop("src");
              holder.css({
                backgroundImage: "url(" + imageSrc + ")"
              });
            });
          };
          var initBgCover = initBgCover2;
          initBgCover2();
          var winWidth = $(window).width();
          $('a.js-has-smooth[href*="#"]:not([href="#"])').click(function() {
            if (location.pathname.replace(/^\//, "") === this.pathname.replace(/^\//, "") && location.hostname === this.hostname) {
              var target = $(this.hash);
              target = target.length ? target : $("[name=" + this.hash.slice(1) + "]");
              if (target.length) {
                const offset = winWidth > 991 ? 50 : 0;
                $("html, body").animate(
                  {
                    scrollTop: target.offset().top - offset
                  },
                  1e3
                );
                return false;
              }
            }
          });
          var swiper = new Swiper(".services-slider", {
            slidesPerView: 3,
            spaceBetween: 30,
            navigation: {
              nextEl: ".swiper-button-next",
              prevEl: ".swiper-button-prev"
            },
            breakpoints: {
              320: {
                slidesPerView: 1
              },
              768: {
                slidesPerView: 2
              },
              1024: {
                slidesPerView: 3
              }
            }
          });
          var swiper = new Swiper(".gallery-slider", {
            slidesPerView: 1,
            spaceBetween: 0,
            navigation: {
              nextEl: ".gallery-slider-button-next",
              prevEl: ".gallery-slider-button-prev"
            },
            breakpoints: {
              640: {
                slidesPerView: "auto"
              }
            }
          });
          $("#backToTop").on("click", function() {
            $("html, body").animate({ scrollTop: 0 }, 1e3);
          });
        } catch (err) {
          console.log(err);
        }
      }
    };
    $(function() {
      ss.init();
    });
  })(jQuery);
})();
